# -*- coding: utf-8 -*-from django import templateregister = template.Library()import syssys.path.append("/home/root2/mptt/readtags")from config_project import *def get_parent(tagid):	parent_id=items.find_one({'_id':ObjectId(tagid)},{'parent':1, '_id':0})	parent=items.find_one({'_id':ObjectId(parent_id['parent'])},{'name':1,'parent':1})	parent_bus=items.find_one({'_id':ObjectId(parent['parent'])},{'name':1})	connection.end_request()	return "%s | %s" % (parent_bus['name'], parent['name'])register.filter('get_parent', get_parent)